#!/usr/bin/env node
var eyes = require('eyes'),
    stdin = process.openStdin(),
    d = '';

stdin.setEncoding('utf8');

stdin.on('data', function (chunk) {
  d += chunk;
});

stdin.on('end', function () {
    try {
        if (d[0] !== '{' && d[0] !== '[') {
            d = d
                .replace(/^\w+\(/, '')
                .replace(/\);?$/, '');
        }
        eyes.inspect(JSON.parse(d));
    } catch(e) {
        eyes.inspect(e);
    }
});
